INSERT INTO "startups" VALUES(19,'Clever','san francisco',2055,'education',3,'Online learning software in K-12 education is one of the fastest growing markets on the planet, and Clever is quickly becoming the infrastructure these apps depend on.  Today, many of the biggest and most innovative applications in education are already powered by Clever.  We''re now moving data for 16 million students in 1 in 4 schools in America - and our platform is just getting started.','https://clever.com','https://d1qb2nb5cznatu.cloudfront.net/startups/i/114426-35f998fe322ec70969f2c3c932b2caf3-medium_jpg.jpg?buster=1420826004',1);
