INSERT INTO "startups" VALUES(1174,'We&Co_','atlanta',108,'SaaS',3,'We&Co provides People Analytics to the Hospitality industry to allow them to better recruit, retain, and motivate staff. Our tools gather data on hospitality professionals ranging from work history to social data to point of sale data. This allows our customers to make better, more informed hiring decisions and retain their key, star employees.','http://www.weand.co','https://d1qb2nb5cznatu.cloudfront.net/startups/i/16686-a3edc2ec3b43caf24bb4b89c10ed830e-medium_jpg.jpg?buster=1315736117',10);
INSERT INTO "startups" VALUES(1175,'Usable Health','atlanta',104,'digital media',3,'SmartMenus are web-based ordering kiosks and tablets that serve as virtual cashiers and virtual dietitians for the restaurant industry.','http://usablehealth.com/','https://d1qb2nb5cznatu.cloudfront.net/startups/i/18627-4860b8d723c0c9eac800c8aebce6f0b9-medium_jpg.jpg?buster=1315735274',10);
