INSERT INTO "startups" VALUES(1190,'Monsieur','atlanta',73,'robotics',2,'Monsieur is a cocktail ordering, preparation and service system that enables users to enjoy bartender quality cocktails in seconds and tabulates detailed data on the businesses alcoholic beverage operations.
INSERT INTO "startups" VALUES(1191,'TOHL','atlanta',71,'water',2,'We are a global water company, driven to improve the quality and quantity of water of the more than 700 million people without access to reliable water sources worldwide. We offer a variety of services ranging from water consulting and feasibility studies to the engineering and construction of water systems.  In the projects we pursue we strive to preserve the environment through sustainable practices, while improving the lives of local communities members by involving them directly in the project during the design, implementation, and maintenance phases.','http://theTOHL.com','https://d1qb2nb5cznatu.cloudfront.net/startups/i/75349-382ad8b59c9b2a4c2770f0be7a4b1b87-medium_jpg.jpg?buster=1363822522',10);
